import { useState } from "react";
import { ProductSaleModel } from "../api/productModels";

export interface ProductSalesTableProps {
  readonly sales: ProductSaleModel[];
}

function compareSales(saleA: ProductSaleModel, saleB: ProductSaleModel, key: keyof ProductSaleModel) {
  if (key === 'weekEnding') {
    const [dateA, dateB] = [new Date(saleA[key]), new Date(saleB[key])];
    if (dateA < dateB) {
      return -1;
    } else if (dateA > dateB) {
      return 1;
    } else {
      return 0;
    }
  } else {
    return saleA[key] - saleB[key];
  }
}

export default function ProductSalesTable(props: ProductSalesTableProps) {
  const [sales, setSales] = useState(props.sales);
  const sortSales = function(key: keyof ProductSaleModel) {
    setSales(sales.sort((a, b) => a[key] - b[key]));
  }

  return (
    <table>
      <tr>
        <th scope="col">Week Ending</th>
        <th scope="col">Retail Sales</th>
        <th scope="col">Wholesale Sales</th>
        <th scope="col">Units Sold</th>
        <th scope="col">Retailer Margin</th>
      </tr>
      <tbody>
        { sales.map(sale => (
          <tr>
            <td>{ sale.weekEnding }</td>
            <td>{ sale.retailSales }</td>
            <td>{ sale.wholesaleSales }</td>
            <td>{ sale.unitsSold }</td>
            <td>{ sale.retailerMargin }</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
